<script>
import Header from '@/components/Header.vue'
import Footer from '@/components/Footer.vue'
import artHobbies from '@/components/objects/art&hobbies.json'

export default {
    name: 'ArtHobbies',
    components: {
        Header, Footer
    },
    data() {
        return {
            artHobbies,
            search: '',
            minPrice: 0,
            maxPrice: 100,
        };
    },
    computed: {
        filteredProducts() {
            return this.artHobbies.filter(product => {
                const searchTerm = this.search.toLowerCase();
                const priceInRange = product.price >= this.minPrice && product.price <= this.maxPrice;
                return (product.name.toLowerCase().includes(searchTerm) && priceInRange);
            });
        }
    },
    methods: {
        toProduct(x){
                localStorage.setItem("name", artHobbies[x].name);
                localStorage.setItem("id", artHobbies[x].id);
                localStorage.setItem("price", artHobbies[x].price);
                localStorage.setItem("descriprion", artHobbies[x].description);
                localStorage.setItem("img", artHobbies[x].img);
                localStorage.setItem("sale", artHobbies[x].sale);
                return window.location.href = '/#/product';
        }
    }
}
</script>

